import type { u32 } from '@5ire/types';
import type { AccountId, Balance, BalanceOf, BlockNumber, StrikeCount } from '@5ire/types/interfaces';
import type { PalletSocietyBid, PalletSocietyBidKind, PalletSocietyVote, PalletSocietyVouchingStatus } from '@5ire/types/lookup';
export interface DeriveSociety {
    bids: PalletSocietyBid[];
    defender?: AccountId;
    hasDefender: boolean;
    head?: AccountId;
    founder?: AccountId;
    maxMembers: u32;
    pot: BalanceOf;
}
export interface DeriveSocietyCandidate {
    accountId: AccountId;
    kind: PalletSocietyBidKind;
    value: Balance;
    isSuspended: boolean;
}
export interface DeriveSocietyMember {
    accountId: AccountId;
    isDefenderVoter: boolean;
    isSuspended: boolean;
    payouts: [BlockNumber, Balance][];
    strikes: StrikeCount;
    vote?: PalletSocietyVote;
    vouching?: PalletSocietyVouchingStatus;
}
